import React from "react"
import './App.css';
import {useState, useEffect} from "react"
import NavBar from "./components/NavBar/NavBar";

function App() {
  const CLIENT_ID = "043e7807ac214928a8c68e3ece5ebb0f"
  const REDIRECT_URL = "http://localhost:3000"
  const AUTH_ENDPOINT = "https://accounts.spotify.com/authorize" 
  const SPACE_DELIMITER = "%20"
  const SCOPES = ["user-library-read", "user-top-read"]
  const SCOPES_URL_PARAM = SCOPES.join(SPACE_DELIMITER)


  // const [token, setToken] = useState("")

  // useEffect(() => {
  //   const hash  = window.location.hash
  //   let token = window.localStorage.getItem("token")

  //   if (!token && hash) {
  //     token = hash.substring(1).split("&").find(elem => elem.startsWith("access_token")).split("=")[1]

  //     window.location.ash = ""
  //     window.localStorage.setItem("token", token)

  //   }
  //   setToken(token)

  // }, [])

  const getReturnedParamsFromSpotifyAuth = (hash) => {
    const stringAfterHashtag = hash.substring(1);
    const paramsInUrl = stringAfterHashtag.split("&");
    const paramsSplitUp = paramsInUrl.reduce((accumulater, currentValue) => {
      console.log(currentValue);
      const [key, value] = currentValue.split("=");
      accumulater[key] = value;
      return accumulater;
    }, {});

    return paramsSplitUp;
  };

  const logout = () => {
    setToken("")
    window.localStorage.removeItem("token")
  }

  useEffect(() => {
    if (window.location.hash) {
      const { access_token, expires_in, token_type } =
        getReturnedParamsFromSpotifyAuth(window.location.hash);

      localStorage.clear();

      localStorage.setItem("accessToken", access_token);
      localStorage.setItem("tokenType", token_type);
      localStorage.setItem("expiresIn", expires_in);
    }
  });

  const handleLogin = () => {
    window.location = `${SPOTIFY_AUTHORIZE_ENDPOINT}?client_id=${CLIENT_ID}&redirect_uri=${REDIRECT_URL_AFTER_LOGIN}&scope=${SCOPES_URL_PARAM}&response_type=token&show_dialog=true`;
  };

  return (
    <div className="App">
      <NavBar />
      <div className = "App-header">
        <h3>
          Welcome to Spotify Recap, where you can check your Spotify data!!!
        </h3>
          {!token ?
          <button onClick = {handleLogin}>Log in!</button>
            // <a href={`${AUTH_ENDPOINT}?client_id=${CLIENT_ID}&redirect_ur=${REDIRECT_URI}&response_type=${RESPONSE_TYPE}`}>Login
            //   to Spotify</a>
              : <button onClick={logout}>Logout</button>}
      </div>

    </div>
  );
}

export default App;
